           WHEN ''DATE'' THEN CONVERT(NVARCHAR(MAX),@Value,126)
           WHEN ''DATETIME'' THEN CONVERT(NVARCHAR(MAX),@Value,126)
           WHEN ''DATETIME2'' THEN CONVERT(NVARCHAR(MAX),@Value,126)
