           WHEN ''SMALLDATETIME'' THEN CONVERT(NVARCHAR(MAX),@Value,126)
           WHEN ''TIME'' THEN CONVERT(NVARCHAR(MAX),@Value,126)
           WHEN ''BINARY'' THEN CONVERT(NVARCHAR(MAX),@Value,1)
