  SET @Cmd = REPLACE(@Cmd, ''$$SCHEMA_NAME$$'', QUOTENAME(@SchemaName));
  SET @Cmd = REPLACE(@Cmd, ''$$VIEW_NAME$$'', QUOTENAME(@ViewName));
  SET @Cmd = REPLACE(@Cmd, ''$$TRIGGER_NAME$$'', QUOTENAME(@TriggerName));
